import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'
import { Home3 } from './components/Home3'
import { Login } from './components/Login'
import { Register } from './components/Register'
import { Profile } from './components/Profile'
import {Footer} from './components/Footer'
import { Nav2 } from './components/Nav2'
import React, { useState, useEffect } from 'react'
import './App.css'
const api = "http://localhost:3001/"

function App( props ) {
  const [user, setUser] = useState({})
  const [loggedin, setLoggedin] = useState(false)
  // const [games, setGames] = useState([])
  // const history = useHistory()

  useEffect(() => {  //matches user in system(if one) with session user, passes user info
    findMe()
  }, [])

  const findMe = () => {    
    fetch("/me")
      .then((res) => res.json())
      .then((data) => {
        console.log("current user: ", data)
        if (!data.error) {
          setUser(data)
          setLoggedin(true)
            }
          }  
        )
      .catch((err) => console.log("error =", err))
  }

   const handleLogout = (e) => {
    e.preventDefault();
     const delObj = {
       method: "DELETE"
      }
      fetch(api + "logout", delObj)
      .then((res) => res.json())
      .then((res) => {
        setLoggedin(false)
        setUser({})
        console.log(res)
      })
      .catch((err) => console.log("error =", err))
   }

  //  const submitGameSearch = (e) => {
  //     e.preventDefault()
  //     const search = e.target.firstElementChild.value
  //     fetch("https://www.cheapshark.com/api/1.0/games?title=" + search + "&limit=60&exact=0")
  //     .then((res) => res.json())
  //     .then((games) => {
  //       setGames(games)
  //       console.log(games)
  //       history.push("/games")
  //       games.map((game) => <GameCards game={game} />)
  //     })
  //     .catch((err) => console.log("error =", err))
  //  }

  return (
      <Router>
        <div className="App">
          <div className="container">
              <Nav2 loggedin={loggedin} handleLogout={handleLogout} />
              <Switch>
                <Route exact path ="/" component={Home3} />
                {/* <Route exact path ="/alerts">
                {loggedin ? <AlertsContainer user={user} /> 
                  : <Login setUser={setUser} setLoggedIn={setLoggedin} /> }
                  </Route> */}
                {/* <Route exact path ="/deals" component={DealsContainer} /> */}
                <Route exact path ="/login">
                  <Login setUser={setUser} setLoggedIn={setLoggedin} />
                </Route>
                <Route exact path ="/register">
                  <Register setUser={setUser} setLoggedin={setLoggedin} />  
                </Route>
                <Route exact path ="/profile"> 
                  {loggedin ? <Profile user={user} setLoggedin={setLoggedin} /> 
                        : <Login setUser={setUser} setLoggedIn={setLoggedin} /> }
                </Route> 
                {/* <Route component={NoMatch} /> */}
              </Switch>
              <Footer />
          </div>

        </div>
      </Router>
  );
}

export default App;
