import React, { useState } from 'react'
const api = "http://localhost:3001/"

export const Collection = () => {
    const [collection, setCollection] = useState("")

    const handleChange = (e) => {
        if (e.target.name === "collection") setCollection(e.target.value)
    }

    const createCollection = () => {
        fetch(api + "collections", {
            method: 'POST',
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(collection)
        }).then((res) => res.json())
        .then((res) => {
          console.log(res)
          setCollection({})
        })
      }
    // const [collectionItems, setCollectionItems] = useState([])

return (
    <div>
        <h1>Your Collection</h1>
        <form onSubmit={createCollection} className='poem-form'>
          <input
            placeholder='Collection Name'
            value={collection}
            onChange={handleChange}
            name='collection'
            className='poem-input'
            type="collection"
            required

          />
                  <button onClick={createCollection} className='poem-button'>Create Collection</button>
          </form>
        {/* <div>{collectionItems.length ===  0 && <h2>Empty</h2>}</div> */}
    </div>
    )
}
