import React, { Component } from 'react';
import axios from 'axios';

export default class ReviewForm2 extends Component {
    constructor(props) {
        super(props);

        this.state = {
            body: ""
        }

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.setState({
            body: e.target.value
        });
    }

    handleSubmit(props) {
        e.preventDefault();
        axios.post("http://localhost:3001/reviews", {
            review: {
            body: this.state.body
            }
        },
        // { withCredentials: true}
        )
        .then(() => {
            props.onSubmit({
              body: this.state.body
            //   user: user,
            });
            // setInput('');
            // userSubmit('')
            console.log({
              body: this.state.body
            //   user: user,
            })
          })
        }

    render() {
        return (
            <div>
            <form onSubmit={this.handleSubmit} className='poem-form'>
            <input 
                    type="poem" 
                    name="poem" 
                    placeholder='Contribute a line'
                    value={this.state.body} 
                    onChange={this.handleChange} 
                    className='poem-input'
                    required 
                    />
                  <button className='poem-button' type='submit'>Submit</button>
            </form>
            </div>
            );

    }
}
