import React, { Component } from 'react';
import axios from 'axios';

export default class ReviewForm2 extends Component {
    constructor(props) {
        super(props);

        this.state = {
            body: ""
        }

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    handleSubmit(e) {
        axios.post("http://localhost:3001/reviews", {
            review: {
            body: this.state.body
            }
        },
        // { withCredentials: true}
        )
        .then(response => {
            if (response.data.status === 'created') {
            this.props.handleAuth(response.data);
            }
            console.log("registration res", response);
        })
        .catch(error => {
            console.log("registration error", error);
        });
        console.log("form submitted");
        e.preventDefault();
    }

    render() {
        return (
            <div>
            <form onSubmit={this.handleSubmit} className='poem-form'>
            <input 
                    type="poem" 
                    name="poem" 
                    placeholder='Contribute a line'
                    value={this.state.body} 
                    onChange={this.handleChange} 
                    required 
                    />
                  <button className='poem-button'>Submit</button>
            </form>
            </div>
            );

    }
}
