  },[]);

  const [detail, setDetail] = useState({
    data: {},
  });

  const [collectionItems, setCollectionItems] = useState([])

  const onAdd = (detail) => {
    const exist = collectionItems.find(x => x.id === detail.id);
    if(exist) {
        setCollectionItems([...collectionItems, {...detail}])
    }
}

